#include <stdio.h>
#define SIZE 100

int main()
{
    int arr[SIZE];
    int i, j;
    int count;
    for (i = 0; i < SIZE; i++)
    {
        scanf("%d", &arr[i]);
    }
    for (i = 0; i < SIZE; i++)
    {
        count = 0;      // number of occurrences of arr[i]
        for (j = 0; count < 2 && j < SIZE; j++)
        {
            if (arr[i] == arr[j])
                count++;
        }
        if (count < 2)  // appears only once
            printf("%d\n", arr[i]);
    }

    return 0;
}
